<script>    $(document).ready(function() {        const priceSwitch = $('#plan-toggle');        // Function to toggle between monthly and yearly pricing views        function togglePrices() {            if (priceSwitch.is(':checked')) {                // Show monthly pricing by default                $('.pricing-yearly').hide();                $('.pricing-monthly').show();                $('.plan-type').val(`{{ \App\Constants\PlanType::MONTHLY }}`);            } else {                // Show yearly pricing, but monthly should remain visible                $('.pricing-yearly').show();                $('.pricing-monthly').hide();                $('.plan-type').val(`{{ \App\Constants\PlanType::YEARLY }}`);            }        }        // Initial call to display the correct prices        togglePrices();        // Update the prices when the toggle switch is changed        priceSwitch.change(function() {            togglePrices();        });    });</script>