@endsection@push('user-script')    <script>        $(document).on('click', '.task-show', function() {            var url = $(this).data('show-url');            $('#task-show-data').html('<div class="text-center"><div class="spinner-border text-primary" role="status"></div></div>');            $.ajax({                url: url,                method: 'GET',                success: function(response) {                    $('#task-show-data').html(response);                    $('#task-show').modal('show');                }            });        });    </script>@endpush