<script>    // Retrieve the daterange value from the request, passed from the server (if available)    let dateRange = '{{ request('daterange') }}';    // If daterange is provided, split it into start and end dates    if (dateRange) {        // Split the daterange into two dates (start and end), assuming format 'YYYY-MM-DD,YYYY-MM-DD'        let dates = dateRange.split(',');        // Parse the start and end dates using the expected format (ISO 8601 'YYYY-MM-DD')        let start = moment(dates[0], 'YYYY-MM-DD'); // Convert first date to moment object        let end = moment(dates[1], 'YYYY-MM-DD');   // Convert second date to moment object        // Use the helper function to initialize the date range picker with the provided dates        setupDateRangePicker('#reportrange', start, end, dateRangeCallback);    }</script>