<?php $__env->startSection('title', __('User Transaction')); ?><?php $__env->startSection('user_sub_content'); ?>    <div class="card border-0 mb-4">        <div class="card-body">            <div class="table-responsive rounded">                <table class="table table-bordered  table-striped mb-0">                    <thead class="table-light">                    <tr>                        <th><?php echo e(__('Description') .' | ' . __('TXN ID')); ?></th>                        <th><?php echo e(__('Amount') .' | ' . __('Type')); ?></th>                        <th><?php echo e(__('Fee')); ?></th>                        <th><?php echo e(__('Status')); ?></th>                        <th><?php echo e(__('Time')); ?></th>                    </tr>                    </thead>                    <tbody>                    <?php $__currentLoopData = $transactions; $__env->addLoop($__currentLoopData); foreach($__currentLoopData as $transaction): $__env->incrementLoopIndices(); $loop = $__env->getLastLoop(); ?>                        <?php                            // Define the status color map                            $statusColors = [                                \App\Constants\TxnStatus::PENDING => 'info',                                \App\Constants\TxnStatus::COMPLETED => 'success',                                \App\Constants\TxnStatus::FAILED => 'danger'                            ];                            // Get the color from the status, fallback to 'blue'                            $color = $statusColors[$transaction->status] ?? 'blue';                            // Define the prefix based on transaction type and status                            $prefix = null;                            if ($transaction->status == \App\Constants\TxnStatus::COMPLETED) {                                $prefix = match ($transaction->type) {                                    \App\Constants\TxnType::DEPOSIT,\App\Constants\TxnType::ADD_BALANCE => '+',                                    \App\Constants\TxnType::SUBTRACT_BALANCE => '-',                                    default => null,                                };                            }                            // Cache repeated attribute calls                            $txid = ucwords($transaction->txid);                            $methodType = ucwords($transaction->method_type);                            $transactionType = title($transaction->type);                            $transactionAmount = $prefix . $transaction->amount . ' ' . $transaction->currency;                            $fee = $transaction->fee . ' ' . $transaction->currency;                            $statusText = strtoupper($transaction->status);                            $createdAtTime = $transaction->created_at_time;                            $createdAtDiff = $transaction->created_at->diffForHumans();                        ?>                        <tr class="align-middle">                            <td>                                <div><?php echo e($transaction->description); ?></div>                                <div class="text-muted small"><?php echo e($txid); ?></div>                            </td>                            <td>                                <div class="text-<?php echo e($prefix == '-' ? 'danger' : $color); ?>">                                    <?php echo e($transactionAmount); ?>                                </div>                                <div class="text-muted small"><?php echo e($transactionType); ?></div>                            </td>                            <td>                                <?php echo e($fee); ?>                            </td>                            <td>                                <span class="badge bg-<?php echo e($color); ?> text-<?php echo e($color); ?>-fg"><?php echo e($statusText); ?></span>                            </td>                            <td>                                <div><?php echo e($createdAtTime); ?></div>                                <div class="text-muted small"><?php echo e($createdAtDiff); ?></div>                            </td>                        </tr>                    <?php endforeach; $__env->popLoop(); $loop = $__env->getLastLoop(); ?>                    </tbody>                </table>            </div>        </div>        <div class="d-flex justify-content-end me-4 mb-2">            <?php echo e($transactions->links()); ?>        </div>    </div><?php $__env->stopSection(); ?>
