<div class="col-12">    <div class="card">        <div class="card-header">            <h3 class="card-title"><?php echo e(__('Recent Transactions')); ?></h3>        </div>        <div class="card-body">            <div class="table-responsive">                <table class="table card-table table-vcenter text-nowrap datatable">                    <thead>                    <tr>                        <th><?php echo e(__('Description') .' | ' . __('TXN ID')); ?></th>                        <th><?php echo e(__('Method') .' | ' . __('Method Type')); ?></th>                        <th><?php echo e(__('Amount') .' | ' . __('Type')); ?></th>                        <th><?php echo e(__('Fee')); ?></th>                        <th><?php echo e(__('Status')); ?></th>                        <th><?php echo e(__('Time')); ?></th>                    </tr>                    </thead>                    <tbody>                    <?php $__currentLoopData = $recentTransactions; $__env->addLoop($__currentLoopData); foreach($__currentLoopData as $transaction): $__env->incrementLoopIndices(); $loop = $__env->getLastLoop(); ?>                        <?php                            $color = ['pending' => 'blue', 'completed' => 'green', 'failed' => 'red'][$transaction->status] ?? 'blue';                        ?>                        <tr>                            <td>                                <div><?php echo e($transaction->description); ?></div>                                <div class="text-secondary"><?php echo e(ucwords($transaction->txid)); ?></div>                            </td>                            <td>                                <?php echo e($transaction->method); ?>                                <div class="text-secondary"><?php echo e(ucwords($transaction->method_type)); ?></div>                            </td>                            <td>                                <div class="text-<?php echo e($color); ?>">                                    <?php echo e(($transaction->status == \App\Constants\TxnStatus::COMPLETED && $transaction->type == \App\Constants\TxnType::DEPOSIT ? '+' : '') . $transaction->amount . ' ' . $transaction->currency); ?>                                </div>                                <div class="text-secondary"><?php echo e(title($transaction->type)); ?></div>                            </td>                            <td>                                <?php echo e($transaction->fee . ' ' . $transaction->currency); ?>                            </td>                            <td>                                <span class="badge bg-<?php echo e($color); ?> text-<?php echo e($color); ?>-fg"><?php echo e(strtoupper($transaction->status)); ?></span>                            </td>                            <td>                                <div><?php echo e($transaction->created_at_time); ?></div>                                <div class="text-secondary"><?php echo e($transaction->created_at->diffForHumans()); ?></div>                            </td>                        </tr>                    <?php endforeach; $__env->popLoop(); $loop = $__env->getLastLoop(); ?>                    </tbody>                </table>                <?php if(!$recentTransactions->count()): ?>                    <h4 class="text-center text-muted mt-3"><?php echo e(__('No data found')); ?></h4>                <?php endif; ?>            </div>        </div>    </div></div><?php /**PATH /Users/apple/Documents/gratech/resources/views/frontend/user/dashboard/partial/_recent_transactions.blade.php ENDPATH**/ ?>